import { React ,useContext} from 'react'
export const SettingContext = React.createContext()

export default function settings(props) {
    const state={
        display:false,
        numberOfItems:5,
        sort:'item'
    }

    return (
        <div>
            <SettingContext.Provider value={state}>
                {props.children}
            </SettingContext.Provider>
        </div>
    )
}
